import { useSelector } from "react-redux";
import { logo } from "../assets/images";
import { Link, useNavigate } from "react-router-dom";
const Header = () => {
  const navigate = useNavigate();
  const userInfo = useSelector((state) => state.rescueNet.userInfo);
  return (
    <div className="w-full p-10 font-bold">
      <div className="text-white  h-[86px] w-full header-bg flex items-center justify-between px-10 rounded-full">
        <img
          src={logo}
          alt=""
          className="h-[50px]"
          onClick={() => {
            navigate("/");
          }}
        />

        <ul className="flex items-center gap-4">
          <li className="hover:text-lime-300 hover:scale-110 duration-300">
            <Link to={"/"}>Home</Link>
          </li>
          <li className="hover:text-lime-300 hover:scale-110 duration-300">
            <Link to={"https://github.com/adityaraina100/Rescue-Net"}>
              About Us
            </Link>
          </li>
          <li className="hover:text-lime-300 hover:scale-110 duration-300">
            <Link to={"/agencies"}>Agencies</Link>
          </li>
        </ul>
        <Link to={"/login"}>
          <div className="flex items-center gap-2 hover:text-lime-200 hover:scale-110 duration-200">
            <img
              src={
                userInfo
                  ? userInfo.image
                  : "data:image/png;base64,"
              }
              alt="userLogo"
              className="w-10 h-10 rounded-full object-cover "
            />
            {userInfo && (
              <p className="text-base font-titleFont font-semibold underline underline-offset-2">
                {userInfo.name}
              </p>
            )}
          </div>
        </Link>
      </div>
    </div>
  );
};

export default Header;
